sys_ui_action.script:
function showConfirm() {
    // Team WSR report
    var sysIdList = g_list.getChecked();
    var getStatus = new GlideAjax("global.wsr_utils");
    getStatus.addParam('sysparm_name', 'getStatus');
    getStatus.addParam('sysparm_sysIdList', sysIdList);
    getStatus.getXML(getAnswer);

}

function alertTheUser(res) {
    var ans = res.responseXML.documentElement.getAttribute("answer");
    alert(ans);
    window.open('https://dev105977.service-now.com/sys_email_list.do?sysparm_query=mailbox%3Doutbox%5Esys_created_onONToday@javascript:gs.beginningOfToday()@javascript:gs.endOfToday()&sysparm_first_row=1&sysparm_view=outbox', '_blank');
}

function getAnswer(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer");
    var resObj = JSON.parse(answer);
    var wSRInfo = 'Team has worked on ' + resObj.totalCount + ' tickets in which resolved count is ' + resObj.resolved + ' and 0 work orders  (' + resObj.incidents + ' - Incident | ' + resObj.extraService + ' - Extra service | ' + resObj.enquiry + ' - Query). The remaining  tickets status are [(a) Waiting for internal Approval: ' + resObj.internalApproval + ' | (b)Waiting for 3rd party vendor to take action: ' + resObj.thirdParty + ' | (c) Monitoring: ' + resObj.monitoring + ' |(d) Additional info needed from user: ' + resObj.additionalInfoNeededFromUser + ' |(e) Internal dependency: ' + resObj.internalDependency + ' | (f) In progress : ' + resObj.inProgress + ' | (g) on hold : ' + resObj.onHold + '  |(h) user to validate : ' + resObj.userToValidate + ']';

    var newWSRInfo = '<ol style="list-style: decimal">  <li>    <p>      The team has diligently attended to a total of ' + resObj.totalCount + '      tickets within the specified week.    </p>  </li>  <li>    <p>   Among these,  ' + resObj.resolved + ' tickets have been effectively resolved, while ' + resObj.userToValidate + ' are currently awaiting validation from the user.</p>  </li>  <li>   <p>Notably, there were XX work orders addressed during this period.</p>  </li>  <li>    <p>The breakdown of ticket types is as follows:</p>    <ul style="list-style: disc">      <li>Incidents: ' + resObj.incidents + '</li>      <li>Extra Service Requests: ' + resObj.extraService + '</li>      <li>Queries: ' + resObj.enquiry + '</li>    </ul>  </li>  <li>    <p>The status of all the tickets is categorized as follows:</p>    <ul style="list-style: disc">      <li>Awaiting Internal Approval: ' + resObj.internalApproval + '</li>      <li>        Awaiting Action from Third-party Vendors: ' + resObj.thirdParty + '      </li>      <li>Under Monitoring: ' + resObj.monitoring + '</li>      <li>        Pending Additional Information from Users: ' + resObj.additionalInfoNeededFromUser + '      </li>      <li>Internal Dependencies: ' + resObj.internalDependency + '</li>      <li>Currently In Progress: ' + resObj.inProgress + '</li>      <li>On Hold: ' + resObj.onHold + '</li>      <li>Pending User Validation: ' + resObj.userToValidate + '</li>    </ul>  </li></ol>';

    console.log(wSRInfo);

    var individualInfo = new GlideAjax("global.wsr_utils");
    individualInfo.addParam('sysparm_name', 'individualReportWSR');
    individualInfo.addParam('sysparm_wsrData', newWSRInfo);
    individualInfo.getXML(alertTheUser);

}
